import React, {Component} from 'react';
import video from "../../Assets/blackHistory1.mp4";
import video2 from "../../Assets/blackHistory2.mp4";

class PhotoGallery extends Component {

  componentDidMount() {
    let myvid = document.getElementById('myvideo');
    let myvids = [
      video,
      video2
    ];
    let activeVideo = 0;

    myvid.addEventListener('ended', function(e) {
      // update the active video index
      activeVideo = (++activeVideo) % myvids.length;
    console.log('------------------------>', );
      // update the video source and play
      myvid.src = myvids[activeVideo];
      myvid.play();
    });
  }

  render() {
    const myCallback = () => {
      let myvids = [
        video,
        video2
      ];
      let activeVideo = 0;

        // update the active video index
        activeVideo = (++activeVideo) % myvids.length;

        // update the video source and play
        myvid.src = myvids[activeVideo];
        myvid.play();
    };

    return (
      <div className={'photo'}>
        <div className={'photoGallery'}>
          Photo Gallery <video autoPlay onEnded={() => myCallback()} id={'myvideo'} src={video} width="370" height="225" controls>
        </video>
        </div>
        <div className={'gallery'}>
          Coming Soon <span>&#129335;&#127997;</span><span>&#129335;&#127997;</span><span>&#129335;&#127997;</span>
        </div>
      </div>
    );
  }
}

export default PhotoGallery;
